import React,{useState} from "react";
import { Link,useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios"

function Register() {

const naviagate = useNavigate() ;

  const [values, setValues] = useState({
    name:"",
    email:"",
    password:"",
  });

  const generateError = (err) =>toast.error(err,{
    position : "top-center"
  })

  const handleSubmit =async (e)=>{
    e.preventDefault();
    try {
      const {data} = await axios.post("http://localhost:4000/register",{
        ...values
      },{
        withCredentials:true,
      })
      
      if (data) {

        if (data.errors) {

          const { email, password ,name} = data.errors;
          if (email) generateError(email);
          else if (password) generateError(password)
          else if (name) generateError(name)

        } else {

          if (data.value) {

            navigate("/admin_panel");
          } else {
            navigate("/homepage");
          }
          // } else {
          //   console.log("data.value /home");
          //   //  navigate("/home")
          // }
        }



      }


    } catch (error) {
      console.log(error,"==========register error");
      
    }
  };



  return (
    <div className="CardBody">
      <div className="container">
        <h2>Register Account</h2>
        <form action="" onSubmit={(e)=>handleSubmit(e)}>
          <div>
            <label htmlFor="name">Name</label>
            <input type="text" name="name" placeholder="Enter your Name" onChange={(e)=>setValues({...values,[e.target.name]: e.target.value})} />
          </div>
          <div>
            <label htmlFor="email">Email</label>
            <input type="email" name="email" placeholder="Enter your Email" onChange={(e)=>setValues({...values,[e.target.name]: e.target.value})} />
          </div>
          <div>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              placeholder="Enter your Password" onChange={(e)=>setValues({...values,[e.target.name]: e.target.value})} />
          </div>

          <button type="submit">Sublimt</button>
          <span>
            Already have an acoount? <Link to="/">Login</Link>{" "}
          </span>
        </form>
        <ToastContainer />
      </div>
    </div>
    
  );
}

export default Register;
