import React,{useState,useEffect} from "react";
import { Link,useNavigate, useParams } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios"

const initialState= {
  name:"",
  email: ""
}



function Update() {

  const [state, setState] = useState(initialState);
  const {name,email} = state
const {id} =useParams();



useEffect(() => {
    if(id){
     
      getSingleUser(id)
    }
}, [id]);





const getSingleUser = async (id) =>{
  const response =await axios.get(`http://localhost:4000/user/${id}`);
  if(response.status===200){
    setState({...response.data[0]})
 

  }
}





  return (
    <div className="CardBody">
    <div className="container innerbody">
      <h2>Register Account</h2>
      <form className="form" action="">
        <div>
          <label htmlFor="name">Name</label>
          <input  className="input" type="text" name="name" placeholder="Enter your Name" value={name} />
        </div>
        <div>
          <label htmlFor="email">Email</label>
          <input  className="input" type="email" name="email" placeholder="Enter your Email" value={email} />
        </div>
        <div>
          <label htmlFor="password">Password</label>
          <input
          className="input"
            type="password"
            name="password"
            placeholder="Enter your Password" 
            />
        </div>

        <button className="button" type="submit">Sublimt</button>
        
      </form>
      <ToastContainer />
    </div>
  </div>
  )
}

export default Update
