import React,{useState} from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios"

function Register() {
  const [values, setValues] = useState({
    name:"",
    email:"",
    password:"",
  });

  const handleSubmit =async (e)=>{
    e.preventDefault();
    try {
      const {data} = await axios.post("http://localhost:4000/register",{
        ...values
      })

      


    } catch (error) {
      console.log(error,"==========register error");
      
    }
  };



  return (
    <div className="CardBody">
      <div className="container">
        <h2>Register Account</h2>
        <form action="" onSubmit={(e)=>handleSubmit(e)}>
          <div>
            <label htmlFor="name">Name</label>
            <input type="text" name="name" placeholder="Enter your Name" onChange={(e)=>setValues({...values,[e.target.name]: e.target.value})} />
          </div>
          <div>
            <label htmlFor="email">Email</label>
            <input type="email" name="email" placeholder="Enter your Email" onChange={(e)=>setValues({...values,[e.target.name]: e.target.value})} />
          </div>
          <div>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              placeholder="Enter your Password" onChange={(e)=>setValues({...values,[e.target.name]: e.target.value})} />
          </div>

          <button type="submit">Sublimt</button>
          <span>
            Already have an acoount? <Link to="/login">Login</Link>{" "}
          </span>
        </form>
        <ToastContainer />
      </div>
    </div>
    
  );
}

export default Register;
